// --- 1. JSON Parse and Validation ---
if (typeof msg.payload === "string") {
    try { 
        msg.payload = JSON.parse(msg.payload); 
    } catch (e) { 
        node.error("Invalid JSON received from MQTT", msg); 
        return [null, null]; 
    }
}

// --- 2. Required fields check ---
if (!msg.payload || msg.payload.value == null || !msg.payload.location || msg.payload.timestamp == null || !msg.payload.sensor_id) {
    node.warn("Missing critical required fields.");
    return [null, null];
}

const value = parseFloat(msg.payload.value); 
const ts_ms = Number(msg.payload.timestamp);

if (isNaN(value) || isNaN(ts_ms)) {
    node.warn("Invalid numerical value or timestamp found.");
    return [null, null];
}

// --- 3. Extract sensor info from topic ---
const parts = (msg.topic || "").split('/').filter(x => x.trim() !== "");
const domain = parts[0] || "unknown";
const sensor_type = parts[2] || "unknown"; 

// --- 4. Threshold table per sensor type ---
const THRESHOLDS = {
    temperature: { min: 18.0, max: 27.0 },
    humidity: { min: 40.0, max: 60.0 },
    water_ph: { min: 5.5, max: 6.5 },
    ec: { min: 1.5, max: 2.5 },
    water_temperature: { min: 18.0, max: 24.0 },
    light: { min: 600, max: 1200 }
};

// --- 5. Check if value exceeds thresholds ---
let alertDataMsg = null;
const threshold = THRESHOLDS[sensor_type];

if (threshold && (value < threshold.min || value > threshold.max)) {
    alertDataMsg = {
        payload: {
            domain: "Smart Agriculture",
            sensor: sensor_type,
            sensor_id: msg.payload.sensor_id,
            location: msg.payload.location,
            value: value,
            min: threshold.min,
            max: threshold.max,
            timestamp: new Date().toISOString(),
            alert: value < threshold.min ? "below_min" : "above_max"
        }
    };
}

// --- 6. Prepare InfluxDB payload ---
const influxPayload = [
    { value: value, time: ts_ms }, // Fields
    { sensor_type: sensor_type, location: msg.payload.location, sensor_id: msg.payload.sensor_id, domain: domain } // Tags
];

msg.payload = influxPayload;
msg.measurement = "environment_readings";

// Output 1: InfluxDB data
// Output 2: alert data (null if within thresholds)
return [msg, alertDataMsg];

// Function Node 2: Telegram Formatting (Input from Function 1, Output to Telegram Sender)

const CHAT_ID = 5525573380; // REPLACE with your actual Telegram Chat ID
let alertPayload = msg.payload;

if (!alertPayload) {
    // No alert, do nothing
    return null;
}

// Determine human-readable alert description
let conditionString = "";
let detailsString = "";

if (alertPayload.alert === "above_max") {
    conditionString = `Value is ${alertPayload.value} (Threshold > ${alertPayload.max})`;
    detailsString = `Sensor exceeded maximum threshold. Immediate action may be required.`;
} else if (alertPayload.alert === "below_min") {
    conditionString = `Value is ${alertPayload.value} (Threshold < ${alertPayload.min})`;
    detailsString = `Sensor fell below minimum threshold. Immediate action may be required.`;
} else {
    // Unknown alert type
    conditionString = `Value is ${alertPayload.value} (Threshold unknown)`;
    detailsString = `Check sensor readings.`;
}

// Construct the final HTML-formatted message
// CORRECTED: The closing tag is </b> (slash BEFORE the 'b')
let telegramMessage = `<b>HYDRO ALERT - CRITICAL</b> \n\n`;
telegramMessage += `<b>DOMAIN:</b> ${alertPayload.domain}\n`;
telegramMessage += `<b>LOCATION:</b> ${alertPayload.location}\n`;
telegramMessage += `<b>SENSOR:</b> ${alertPayload.sensor}\n`;
telegramMessage += `<b>SENSOR_ID:</b> ${alertPayload.sensor_id}\n`;
telegramMessage += `<b>CONDITION:</b> ${conditionString}\n`;
telegramMessage += `<b>DETAILS:</b> ${detailsString}\n`;
// Note: I've removed the extra underscores here, as <i> is enough for italics
// CORRECTED: The closing tag is </i> (slash BEFORE the 'i')
telegramMessage += `<i>Time:  ${alertPayload.timestamp.substring(11, 19)} UTC</i>`;

// Build the payload for Telegram Sender node
msg.payload = {
    chatId: CHAT_ID,
    type: "message",
    content: telegramMessage,
    options: {
        parse_mode: 'HTML' // This is essential for the tags to work
    }
};

return msg;
